using namespace std;

inline int read() {
	int res = 0 ;int neg ;
	while(true){char ch = getchar();if(ch>='0' && ch<='9' || ch=='-'){if(ch=='-') neg = -1;else neg = 1 , res = ch-'0';break;} }
	while(true){char ch = getchar();if(ch>='0' && ch<='9') res*=10 , res+=ch-'0';else break;}
	return res*neg;
}

typedef long long Lint;
typedef double db;
typedef pair<int,int> ii;
typedef pair<int,ii> iii;

const int maxn = 1000020;
const int MOd = 1e9+7;

int a, d, h, k;
int ar[maxn];
Lint H[maxn];

int main() {

	scanf("%d %d",&a,&k); a++;

	for(int i=1;i<=a;i++) {
		scanf("%d",&ar[i]);
	}
	int mini = a + 1;
	Lint t = 0;
	for(int i=a;i>=1;i--) {
		t += ar[i];
		//~ printf("asd %d\n",t);
		t *= 2;
		//~ printf("asd %d\n",t);
		if( t <= -2*k || t >= 2*k ) break;
		H[i] = t;
		mini = i;
		//~ printf("--------------- %d\n",i);
	} t = 0;
	int ans = 0;
	for(int i=1;i<=a;i++) {
		if( i + 1 >= mini ) {
			Lint h = -H[i+1] - t;
			//~ printf("-- %d %d %d\n",H[i+1],t,h);
			if( abs( h ) <= k && !(i==a && h == 0) ) ans++;
		}
		t += ar[i];
		//~ printf("asd %d\n",t);
		if( t%2 == 1 || t%2 == -1 ) break;
		t /= 2;
	}
	printf("%d\n",ans);
	return 0;
}
