#include <bits/stdc++.h>
using namespace std;

const int M = 1e9 + 7;
const int N = 1234567;
string s[N];

int main() {
    int t; cin >> t;
    for (int tt = 0; tt < t; tt++) {
        int n, m;
        cin >> n >> m;
        int x = 0, y = 0;
        for (int i = 0; i < n; i++) {
            cin >> s[i];
            for (int j = 0; j < m; j++) {
                if (s[i][j] == 'P') {
                    x = 1;
                }
                if (s[i][j] == 'A') {
                    y = 1;
                }
            }
        }
        if (x == 0) {
            cout << 0 << "\n";
            continue;
        }
        if (y == 0) {
            cout << "MORTAL\n";
            continue;
        }
        int a = 4;
        for (int i = 0; i < n; i++) {
            int u = -1e9, v = 1e9;
            for (int j = 0; j < m; j++) {
                u = max(u, (int) s[i][j]);
                v = min(v, (int) s[i][j]);
                if (s[i][j] == 'A') {
                    int t = 4;
                    if (i == 0 || i == n - 1) {
                        t--;
                    }
                    if (j == 0 || j == m - 1) {
                        t--;
                    }
                    a = min(a, t);
                }
            }
            if (u == v && u == 'A') {
                if (i == 0 || i == n - 1) {
                    a = min(a, 1);
                }
                else {
                    a = min(a, 2);
                }
            }
        }
        for (int j = 0; j < m; j++) {
            int u = -1e9, v = 1e9;
            for (int i = 0; i < n; i++) {
                u = max(u, (int) s[i][j]);
                v = min(v, (int) s[i][j]);
            }
            if (u == v && u == 'A') {
                if (j == 0 || j == m - 1) {
                    a = min(a, 1);
                }
                else {
                    a = min(a, 2);
                }
            }
        }
        cout << a << "\n";
    }
    return 0;
}
