#include <cstring>
#include <cmath>
#include <cstdio>
#include <cstdlib>
#include <iostream>
#include <algorithm>

#define ull long long

#define x first
#define y second
#define mp std::make_pair
#define pii std::pair<ull,ull>

const int MAXN=1000;

pii operator - (pii A,pii B) { return mp(A.x-B.x,A.y-B.y); }
ull operator * (pii A,pii B) { return A.x*B.y-A.y*B.x; }

pii p[MAXN+10];

int n;

void init()
{
	std::cin>>n;
	int lx,ly,fx,fy;
	for (int i=1;i<=n;i++) {
		int x,y; scanf("%d%d",&x,&y);
		if (i>1) p[i-1]=mp(x-lx,y-ly);
		else fx=x,fy=y;
		lx=x; ly=y; }
	scanf("%d%d",&fx,&fy);
	p[n]=mp(fx-lx,fy-ly);
	int ans=0;
	for (int i=1;i<=n;i++) {
		int nxt=(i==n)?(1):(i+1);
		if (p[i]*p[nxt]>0) ans++; }
	std::cout<<ans<<std::endl;
	return ;
}

int main()
{
	init();
	return 0;
}
