#include<iostream>
#include<cstdio>
#include<algorithm>
using namespace std;
inline int read()
{
	int n=0,f=1,ch=getchar();
	while(ch<'0'||ch>'9')
	{
		if(ch=='-')f=-1;
		ch=getchar();
	}
	while(ch>='0'&&ch<='9')
	{
		n=n*10+ch-'0';
		ch=getchar();
	}
	return n*f;
}
char s[200005];
int ansu[200005],ansv[200005],tmp;
int js[200005],cnt;
bool vis[200005];
int main()
{
	int t,n,ans=0,ans1=0,tsl,las;
	t=read();
	for(int greg=1;greg<=t;greg++)
	{
		n=read();
		scanf("%s",s+1);
		tsl=0;
		for(int i=1;i<=n;i++)if(s[i]=='1')tsl++;
		if(tsl==0||tsl%2==1)
		{
			printf("NO\n");
			continue;
		}
		printf("YES\n");
		tmp=0;
		cnt=0;
		las=0;
		for(int i=1;i<=n;i++)vis[i]=false;
		for(int i=1;i<=n;i++)
		{
			if(s[i]=='0'&&las!=0)
			{
				ansu[++tmp]=i;
				vis[i]=true;
				ansv[tmp]=las;
				las=i;
			}
			if(s[i]=='1')
			{
				if(las!=0)js[++cnt]=las;
				las=i;
			}
		}
		for(int i=1;i<=n;i++)
		{
			if(s[i]=='0'&&vis[i]==false)
			{
				ansu[++tmp]=i;
				ansv[tmp]=las;
				vis[i]=true;
				las=i;
			}
			if(s[i]=='1')break;
		}
		js[++cnt]=las;
		//printf("!!!%d\n",cnt);
		for(int i=2;i<=cnt;i++)
		{
			ansu[++tmp]=js[1];
			ansv[tmp]=js[i];
		}
		for(int i=1;i<=tmp;i++)printf("%d %d\n",ansu[i],ansv[i]);
	}
	return 0;
}
