#include <cstdio>
#include <cstdlib>
#include <cstring>
#include <iostream>
#include <algorithm>
typedef long long ll;
#define rep(i, a, b) for (int i = a; i <= b; ++ i)
using namespace std;
int n, l[500005]; string s[500005];
void solve(int x, int y) {
	int len = min(l[x], l[y]);
	rep(i, 0, len - 1) {
		if (s[x][i] < s[y][i]) return ;
		if (s[x][i] > s[y][i]) {
			l[x] = i;
			return ;
		}
	}
	if (l[x] <= l[y]) return; else l[x] = len;
}
int main() {
	scanf("%d", &n);
	rep(i, 1, n) cin >> s[i], l[i] = s[i].length();
	for (int i = n; i > 1; -- i) solve(i - 1, i);
	rep(i, 1, n) {
		rep(j, 0, l[i] - 1) printf("%c", s[i][j]);
		printf("\n");
	}
	return 0;
}
