#include <cstdio>

int n, m, i, j, r, k, c = 0;
char maj[300][300];

void main() {
	scanf("%d%d%d", &n, &m, &k);
	for(i = 0; i < n; ++i) {
		for(getchar(), j = 0; j < m; maj[i][j++] = getchar()) { }
	}
	for(r = 1; r * 2 < n && r * 2 < m; ++r) {
		for(i = r; i < n - r; ++i) {
			for(j = r; j < m - r; ++j) {
				if(maj[i][j] == '*' && maj[i - r][j] == '*' && maj[i + r][j] == '*'
					&& maj[i][j - r] == '*' && maj[i][j + r] == '*' && ++c == k) {
						++i, ++j;
						printf("%d %d\n%d %d\n%d %d\n%d %d\n%d %d\n", i, j, i - r, j, i + r, j, i, j - r, i, j + r);
						return ;
				}
			}
		}
	}
	puts("-1");
}
