#include<bits/stdc++.h>
#define ll long long
using namespace std;
int N,A[1000006],cnt[1000006],g[1000006],fl[1000006];
ll f[1000006];
void solve(){
	scanf("%d",&N);
	for(int i=1;i<=N;++i)f[i]=cnt[i]=g[i]=fl[i]=0;
	for(int i=1;i<=N;++i)scanf("%d",&A[i]),++cnt[A[i]],fl[A[i]]=1;
	for(int i=1;i<=N;++i)for(int j=2*i;j<=N;j+=i)fl[j]|=fl[i];
	ll res=0ll;
	for(int i=N;i>=1;--i){
		g[i]=0;for(int j=i;j<=N;j+=i)g[i]+=cnt[j];
		f[i]=1ll*g[i]*(g[i]-1)/2ll;
		for(int j=2*i;j<=N;j+=i)f[i]-=f[j];
		if(!fl[i])res+=f[i];
	//	cout<<i<<" "<<f[i]<<" "<<g[i]<<endl;
	} cout<<res<<endl;
}
int main(){
	int t;cin>>t;
	while(t--)solve(); 
    return 0;
}
