#include <bits/stdc++.h>
using namespace std;

#define IOS ios::sync_with_stdio(0); cin.tie(0); cout.tie(0);
#define endl "\n"
#define ll long long

const int mxN=1e5, M=1e9+7;
string s;
int q, c2i[256], c[52], n;
ll dp1[53][mxN/2+1], dp2[53][mxN/2+1], iv[mxN+1], f1[mxN+1], f2[mxN+1], ans[52][52], dp3[53][mxN/2+1];

int32_t main()
{
	IOS;
	for(int i=0; i<26; ++i) {
		c2i['a'+i]=i;
		c2i['A'+i]=i+26;
	}
	cin >> s;
	n=s.size();
	for(char ci : s)
		++c[c2i[ci]];
	iv[1]=1;
	for(int i=2; i<=n; ++i)
		iv[i]=M-M/i*iv[M%i]%M;
	f1[0]=1;
	f2[0]=1;
	for(int i=1; i<=n; ++i) {
		f1[i]=f1[i-1]*i%M;
		f2[i]=f2[i-1]*iv[i]%M;
	}
	dp1[0][0]=dp2[52][0]=1;
	for(int i=0; i<52; ++i) {
		for(int j=0; j<=n/2; ++j) {
			if(j+c[i]<=n/2&&c[i]) {
				dp1[i+1][j+c[i]]=(dp1[i+1][j+c[i]]+dp1[i][j])%M;
			}
			dp1[i+1][j]=(dp1[i+1][j]+dp1[i][j])%M;
		}
	}
	for(int i=51; i>=0; --i) {
		for(int j=0; j<=n/2; ++j) {
			if(j+c[i]<=n/2&&c[i]) {
				dp2[i][j+c[i]]=(dp2[i][j+c[i]]+dp2[i+1][j])%M;
			}
			dp2[i][j]=(dp2[i][j]+dp2[i+1][j])%M;
		}
	}
	//cout << dp2[2][2] << endl;
	ll mult=f1[n/2]*f1[n/2]%M;
	for(int i=0; i<52; ++i)
		mult=mult*f2[c[i]]%M;
	for(int i=0; i<52; ++i) {
		memcpy(dp3[i+1], dp1[i], sizeof(dp1[0]));
		for(int j=i+1; j<52; ++j) {
			for(int k=0; k<=n/2; ++k)
				ans[i][j]=(ans[i][j]+dp3[j][k]*dp2[j+1][n/2-k])%M;
			//if(!i)
			//cout << ans[i][j] << endl;
			ans[i][j]=2*ans[i][j]*mult%M;
			memset(dp3[j+1], 0, sizeof(dp1[0]));
			for(int k=0; k<=n/2; ++k) {
				if(k+c[j]<=n/2&&c[j]) {
					dp3[j+1][k+c[j]]=(dp3[j+1][k+c[j]]+dp3[j][k])%M;
				}
				dp3[j+1][k]=(dp3[j+1][k]+dp3[j][k])%M;
			}
		}
	}
	cin >> q;
	while(q--) {
		int xi, yi;
		cin >> xi >> yi, --xi, --yi;
		xi=c2i[s[xi]];
		yi=c2i[s[yi]];
		if(xi==yi) {
			cout << dp1[52][n/2]*mult%M << "\n";
			continue;
		}
		if(xi>yi)
			swap(xi, yi);
		cout << ans[xi][yi] << "\n";
	}
	return 0;
}
