#include <bits/stdc++.h>

using namespace std;
#define INF 1000000007LL

int n;
int times[128][5];

int score(long long a, long long b) {
    if(a*2 > b) {
        return 500;
    } else if(a*4 > b) {
        return 1000;
    } else if(a*8 > b) {
        return 1500;
    } else if(a*16 > b) {
        return 2000;
    } else if(a*32 > b) {
        return 2500;
    } else {
        return 3000;
    }
}

bool wins(long long t) {
    vector<int> vec(5);
    for(int i=0;i<5;i++) {
        if(times[0][i] == -1) {
            vec[i] = 0;
        } else if (times[1][i] == -1 || (times[1][i] != -1 && times[1][i] > times[0][i])) {
            vec[i] = -1;
        } else {
            vec[i] = 1;
        }
    }
    int score0 = 0, score1 = 0;
    for(int i=0;i<5;i++) {
        long long a = 0, b = 0;
        for(int j=0;j<n;j++) {
            if(times[j][i] != -1) {
                a++;
            }
        }
        if(vec[i] == 1) {
            a += t;
        }
        b = t+n;
        if(times[0][i] != -1) {
            score0 += score(a,b) * (250 - times[0][i]);
        }
        if(times[1][i] != -1) {
            score1 += score(a,b) * (250 - times[1][i]);
        }
    }
    return score0 > score1;
}

int main() {
    cin >> n;
    for(int i=0;i<n;i++) {
        for(int j=0;j<5;j++) {
            cin >> times[i][j];
        }
    }
    for(int i=0;i<50000;i++) {
        if(wins(i)) {
            cout << i << endl;
            return 0;
        }
    }
    cout << -1 << endl;
}
