#include<bits/stdc++.h>
using namespace std;
#define mod 998244353
#define IOS ios::sync_with_stdio(0)
#define N 1000005
#define ls k<<1
#define rs k<<1|1
int T,n,ans,i,j,k,a[N],b[N],l,r,mid,c[N],pos[N],gg,tot; 
int check(int x){
	tot=0;
	for(i=1;i<=n;++i)b[i]=a[i];
	for(i=1;i<=n;++i){
		if(b[i]*2<x)++tot,b[i]=1e9;
	}
	int res=2;
	for(i=1;i<n;++i){
		res=min(res,(b[i]<x)+(b[i+1]<x));
	}
	return res+tot;
}
signed main(){
	IOS;
	cin>>T;
	while(T--){
		cin>>n>>k;
		for(i=1;i<=n;++i)cin>>a[i];
		l=1;r=1000000000;
		while(l<r){
			mid=r-(r-l>>1);
			if(check(mid)<=k)l=mid;
			else r=mid-1;
		}
		cout<<l<<"\n";
	}
}
