void _main() {
    int n, w, m;
    scanf("%d%d%d", &n, &w, &m);
    int guests = m;
    int usedbottles = 0;
    int botid = 1; int fuel = m; int used = 0;
    bool ok = 1;
    vector<vector<pii> > sol;
    rep (i, m) {
        int needfuel = n;
        vector<pii> result;
        while (needfuel > 0) {
            if (fuel > 0) {
                int use = min(fuel, needfuel);
                result.pb(pii(botid, use));

                fuel -= use;
                needfuel -= use;
                ++used;
                if (used > 2) ok = 0;
                if (fuel == 0) { botid++; fuel = m; used = 0;}
            }
            else assert(0);
        }
        sol.pb(result);
    }
    if (!ok) cout << "NO" << endl;
    cout.precision(6);
    if (ok) {
        cout << "YES" << endl;
        rep (i, m) {
            rep (j, ss(sol[i])) { if (j) cout << " ";
                ld myfuel = (ld)sol[i][j].second * w / m;
                cout << fixed << sol[i][j].first << " " << myfuel;
            }
            cout << endl;
        }
    }
}

