#include <bits/stdc++.h>
using namespace std;

#ifdef __LOCAL_DEBUG__
# define _debug(fmt, ...) fprintf(stderr, "\033[94m%s: " fmt "\n\033[0m", \
    __func__, ##__VA_ARGS__)
#else 
# define _debug(...) ((void) 0)
#endif
#define rep(i, n) for (int i=0; i<(n); i++)
#define Rep(i, n) for (int i=1; i<=(n); i++)
#define range(x) begin(x), end(x)
typedef long long LL;
typedef unsigned long long ULL;

#ifdef __LOCAL_DEBUG__
const int a = 1000000000 - 1;

bool ask(int x, int y) {
    static int cnt = 0;
    _debug("ask #%d: %d, %d", cnt++, x, y);
    return x % a >= y % a;
}

#else
bool ask(int x, int y) {
    printf("? %d %d\n", x, y);
    fflush(stdout);
    char buf[4]; scanf("%s", buf);
    assert(buf[0] != 'e');
    return buf[0] == 'x';
}
#endif

int work() {
    if (ask(0, 1)) return 1;
    int l = 1, r;
    while (ask(l * 2, l)) l *= 2;
    r = l * 2 + 1; l++; r = min(r, 1000000000);
    while (r > l) {
        int mid = (l + r) / 2;
        if (ask(mid, (mid + 1) / 2)) l = mid + 1;
        else r = mid;
    }
    return l;
}

int main() {
    char cmd[32];
    while (true) {
        scanf("%s", cmd);
        if (strcmp(cmd, "start") == 0) {
            printf("! %d\n", work());
            fflush(stdout);
        } else break;
    }
    return 0;
}
