using namespace std;
#define fr(i,n) for(int i=0;i<n;i++)
#define fo(i,n) for(int i=1;i<=n;i++)
#define fe(i,n) for(__typeof(n.begin()) i=n.begin();i!=n.end();i++)
int f[2][5020],a[5020],b[5020];
int n,m;
int main()
{
	cin>>n>>m;
	for(int i=0;i<n;i++)
		scanf("%d%*lf",a+i),b[i]=a[i];
	sort(b,b+n);
	for(int i=1;i<=n;i++)
	{
		memset(f[i&1],0,sizeof f[0]);
		for(int j=1;j<=n;j++)
		{
			f[i&1][j]=max(f[i-1&1][j],f[i&1][j-1]);
			if(a[i-1]==b[j-1])
				f[i&1][j]=max(f[i&1][j],f[i-1&1][j-1]+1);
		}
	}
	printf("%d",n-f[n&1][n]);
	return 0;
}
