using namespace std;

double x,y,z;
double vx,vy,vz; 

int main()
{
	cin>>x>>z>>y;
	cin>>vx>>vy>>vz;
	double t=-y/vy;
	double px,pz;
	px=vx*t+x/2;
	pz=vz*t;
	while(px<0) px+=x*2;
	while(px>=x*2) px-=x*2;
	while(pz<0) pz+=z*2;
	while(pz>=z*2) pz-=z*2;
	if(px>x) px=x+x-px;
	if(pz>z) pz=z+z-pz;
	cout<<setiosflags(ios::fixed)<<setprecision(10)<<px<<' '<<pz<<endl;


	return 0;
}
