#include<cstdio>
#include<cstring>
#include<algorithm>
using namespace std;
long long n,m;
int main()
{
    scanf("%I64d %I64d",&n,&m);
    long long ans=0;
    for(int i=1;i<=n;i++)
    {
        long long t1,t2,x,c;
        scanf("%I64d %I64d %I64d %I64d",&t1,&t2,&x,&c);
        long long p=t2-t1;
        if(p<=0)
        ans+=m*x+c;
        else
        {
            long long th=(m+p-1)/p;
            long long gao=th*c;
            ans+=min(m*x+c,gao);
        }
    }
    printf("%I64d\n",ans);
}
