#include<iostream>
#include<vector>
using namespace std;
typedef long long ll;
const int P = 0x3f3f3f3f;
const ll inff = 1e18 + 10;
void solve()
{
    int n, x, ma; ll ans = 0, bas = 1;cin >> n;
    vector<int>a(n + 1, 0); vector<ll>res(n + 1,inff);
    for (int i = 1; i <= n; i++) { cin >> x; if (x < n)a[x]++; }
    while(a[ma])ma++; res[ma] = 0;
    for (int i = ma - 1; i >= 0; i--)for (int j = i + 1; j <= ma; j++)res[i] = min(res[i], res[j] + 1ll * a[i] * j + i - j);
    cout << res[0] << "\n";
}
int main()
{
    ios::sync_with_stdio(0); cin.tie(0);
    int t = 1; cin >> t;
    while (t--)solve();
}
