using namespace std;
const int maxn=100005;
struct edge
{
    int y;
    int value;
    int sign;
    edge * next;
};
edge * li[100005];
edge * new_edge()
{
    static edge a[1000005];
    static int top=0;
    return &a[top++];
}
void insert_edge(int x,int y,int z,int f)
{
    edge * t=new_edge();
    t->y=y;
    t->value=z;
    t->next=li[x];
    t->sign=f;
    li[x]=t;
    t=new_edge();
    t->y=x;
    t->value=z;
    t->next=li[y];
    li[y]=t;
    t->sign=f;
}
priority_queue<pair<long long,int> > que;
int pre[maxn];
long long dis[maxn];
int sum[maxn];
bool visit[maxn];
void dijkstra(int s,int n)
{
    memset(visit,false,sizeof(visit));
    memset(pre,-1,sizeof(pre));
    int i;
    for (i=0;i<=n;i++)
    {
        dis[i]=999999999999999999ll;
    }
    dis[s]=0;
    pre[s]=-1;
    que.push(make_pair(0ll,1));
    for (;!que.empty();)
    {
        int now=que.top().second;
        que.pop();
        if (visit[now]) continue;
        visit[now]=true;
        edge * t;
        for (t=li[now];t!=0;t=t->next)
        {
            if (dis[t->y]>dis[now]+t->value)
            {
                dis[t->y]=dis[now]+t->value;
                if (t->sign==1) pre[t->y]=0; else pre[t->y]=now;
                que.push(make_pair(-dis[t->y],t->y));
            }
            else if (dis[t->y]==dis[now]+t->value)
            {
                if ((t->sign==0)&&(pre[t->y]==0)) pre[t->y]=now;
            }
        }
    }
}
int main()
{
    memset(li,0,sizeof(li));
    #ifdef absi2011
    freopen("input.txt","r",stdin);
    freopen("output.txt","w",stdout);
    #endif
    int n,m,k;
    scanf("%d%d%d",&n,&m,&k);
    int i;
    for (i=0;i<m;i++)
    {
        int x,y,z;
        scanf("%d%d%d",&x,&y,&z);
        insert_edge(x,y,z,0);
    }
    memset(sum,0,sizeof(sum));
    for (i=0;i<k;i++)
    {
        int x=1;
        int y,z;
        scanf("%d%d",&y,&z);
        insert_edge(x,y,z,1);
        sum[y]++;
    }
    dijkstra(1,n+1);
    int ans=0;
    for (i=1;i<=n;i++)
    {
        if (pre[i]!=0) ans+=sum[i]; else ans+=(sum[i]-1);
    }
    printf("%d\n",ans);
    return 0; 
}
