typedef vector<string> vs;
typedef pair<int, int> pii;

#define sz(c) int((c).size())
#define all(c) (c).begin(), (c).end()
#define FOR(i,a,b) for (int i = (a); i < (b); i++)
#define FORD(i,a,b) for (int i = int(b)-1; i >= (a); i--)
#define FORIT(i,c) for (__typeof__((c).begin()) i = (c).begin(); i != (c).end(); i++)

int M, N;
string grid[200];

int main() {
	cin >> M >> N;
	FOR(i,0,M) cin >> grid[i];
	int pos = 0;
	int sum = 0;
	int last = 0;
	FOR(i,0,M) FOR(j,0,N) if(grid[i][j] == 'W') last = i;
	FOR(i,0,M) {
		if(i%2 == 0) {
			int ma = pos;
			FOR(j,pos,N) {
				if(grid[i][j] == 'W' || (i < M-1 && grid[i+1][j] == 'W')) {
					ma = j;
				}
			}
			sum += ma - pos;
			pos = ma;
		} else {
			int mi = pos;
			FORD(j,0,pos) {
				if(grid[i][j] == 'W' || (i < M-1 && grid[i+1][j] == 'W')) {
					mi = j;
				}
			}
			sum += pos - mi;
			pos = mi;
		}
	}
	cout << sum+last << endl;
	return 0;
}
