#include<bits/stdc++.h>
using namespace std;
const int N=100005;
int n,a[N],L[N],R[N],ans,cnt,flag,Max;
int main(){
	scanf("%d",&n);
	for (int i=1;i<=n;i++)scanf("%d",&a[i]);
	L[1]=1;
	for (int i=2;i<=n;i++)
		if (a[i]>a[i-1])L[i]=L[i-1]+1;
		else L[i]=1;
	R[n]=1;
	for (int i=n-1;i;i--)
		if (a[i]>a[i+1])R[i]=R[i+1]+1;
		else R[i]=1;
	for (int i=1;i<=n;i++)Max=max(Max,max(L[i],R[i]));
	for (int i=1;i<=n;i++)
		if (L[i]==Max){
			if (flag){
				puts("0");
				return 0;
			}
			flag=i;
		}
	for (int i=1;i<=n;i++)
		if (R[i]==Max){
			if (flag!=i&&flag){
				puts("0");
				return 0;
			}
			flag=i;
		}
	int i=flag;
	if (L[i]==R[i]&&L[i]%2==1)puts("1");
	else puts("0");
}
