#include <bits/stdc++.h>
#define REP(i, n) for(int i = 0;i < n;++i)
#define FOR(i, a, b) for(int i = a;i < b;++i)
#define pb push_back
#define X first
#define Y second
using namespace std;
typedef long long int ll;
typedef pair<int, int> ii;
const int MAX = 1 << 20;
int n, q;
int shiftp, shiftn;
int out[MAX];

int main() {
	scanf("%d %d", &n, &q);
	int curp = 0;
	int curn = 1;
	REP(i, q) {
		int t;
		scanf("%d", &t);
		if(t == 1) {
			int x;
			scanf("%d", &x);
			shiftp += x;
			shiftn += x;
			if(x % 2) {
				curp = !curp;
				curn = !curn;
			}
		}
		else {
			if(curp) --shiftp;
			else ++shiftp;
			if(curn) --shiftn;
			else ++shiftn;
			curp = !curp;
			curn = !curn;
		}
		shiftp %= n;
		shiftn %= n;
	}
	REP(i, n) {
		int pos = i;
		if(i % 2) pos += shiftn;
		else pos += shiftp;
		out[(pos + n) % n] = i + 1;
	}
	REP(i, n) printf("%d ", out[i]);
	printf("\n");
}
