#include<map>
#include<cstdio>
#include<algorithm>
using namespace std;
const int maxn = 100009;
typedef pair<int, bool> PIB;
map<PIB, int>mp;
map<PIB, int>::iterator it1, it2;
PIB p1, p2;
int main()
{
    int n, i, a, b, ans, mi;
    bool f;
    while(scanf("%d", &n) !=EOF)
    {
        mi=(n+1)>>1;
        ans=1<<20;
        mp.clear();
        for(i=0; i<n; ++i)
        {
            scanf("%d%d", &a, &b);
            mp[make_pair(a, true)]++;
            if(a!=b) mp[make_pair(b, false)]++;
        }
        for(it1=mp.begin(); it1!=mp.end(); ++it1)
        {
            p1=it1->first;
            a=p1.first;
            f=p1.second;
            b=it1->second;
            if(f)
            {
                if(b>=mi) ans=0;
                else
                {
                    it2=mp.find(make_pair(a,false));
                    if(it2!=mp.end())
                    {
                        if(it2->second + b>=mi)
                        {
                            ans=min(ans, mi-b);
                        }
                    }
                }
            }
            else
            {
                if(b>=mi) ans=min(ans, mi);
            }
        }
        if(ans>=n) ans=-1;
        printf("%d\n", ans);
    }
    return 0;
}
