#include<bits/stdc++.h>

int main() {
        std::cin.tie(nullptr)->sync_with_stdio(false);

        int t;
        std::cin >> t;
        while (t--) {
                int n, b, x;
                std::cin >> n >> b >> x;
                std::vector<int> a(n);
                for (auto& i : a) std::cin >> i;
                int sm = std::accumulate(a.begin(), a.end(), 0) + 1;
                std::vector<long long> delt(sm, 0);
                for (int j = 0; j < n; ++j) {
                        int c = a[j];
                        long long cur = 0;
                        for (int i = 1; i <= c; ++i) {
                                delt[i] -= cur;
                                int qq = c % i;
                                long long cc = 1ll * (i - qq) * (c / i) * (c - c / i) + 1ll * qq * (c / i + 1) * (c - c / i - 1);
                                // std::cout << j << ' ' << i << ' ' << cc << std::endl;
                                cc /= 2;
                                cur = std::max(cur, cc);
                                delt[i] += cur;
                        }
                }
                for (int i = 1; i < sm; ++i) delt[i] += delt[i - 1];
                long long ans = 0;
                for (int i = 1; i < sm; ++i) {
                        ans = std::max(ans, delt[i] * b - 1ll * (i - 1) * x);
                }
                std::cout << ans << '\n';
        }
}
