#include<bits/stdc++.h>

using namespace std;

typedef long long ll;
typedef pair<int, int> pii;

#define F first
#define S second

const int MAXN = 1e5 + 10;

int n;
string s, t;
bool vis[MAXN];
int sec[MAXN], sz;
vector<int> adj[MAXN];

void dfs(int v){
	vis[v] = true;
	for (int u:adj[v])
		if (!vis[u])
			dfs(u);
	sec[sz++] = v;
}

pii ans[MAXN];
int cc;
int main(){
	ios::sync_with_stdio(false);
	cin.tie(0);
	cin >> n >> s >> t;
	for (int i = 0; i < n; i++){
		int a = (int)(s[i] - 'a');
		int b = (int)(t[i] - 'a');
		adj[a].push_back(b);
		adj[b].push_back(a);
	}
	for (int v = 0; v < 200; v++)
		if (!vis[v]){
			sz = 0;
			dfs(v);
			for (int i = 0; i + 1 < sz; i++)
				ans[cc++] = {sec[i], sec[i+1]};
		}
	cout << cc << "\n";
	for (int i = 0; i < cc; i++)
		cout << char(ans[i].F+'a') << " " << char(ans[i].S+'a') << "\n";
	return 0;
}
