#include<bits/stdc++.h>
using namespace std;
#define N 200020
typedef long long ll;
char s[N],t[N];
int n;
void Solve(){
	vector<int> a,b;
	cin>>n;
	cin>>(s+1);
	cin>>(t+1);
	for(int i=1;i<n;++i){
		if(s[i]!=s[i+1]){
			a.push_back(i);
		}
		if(t[i]!=t[i+1]){
			b.push_back(i);
		}
	}
	if(s[1]!=t[1]||a.size()!=b.size()){
		cout<<-1<<'\n';
		return;
	}
	ll ans=0;
	for(int i=0;i<(int)a.size();++i){
		ans+=abs(a[i]-b[i]);
	}
	cout<<ans<<'\n';
}
int main(){
	ios::sync_with_stdio(false);
	cin.tie(0);
	int T;
	cin>>T;
	while(T--)Solve();
	return 0;
}
