#include <stdio.h>
const int mod = 1000000007;
int n, m, ans, dp[2001][2001], flen[2001], fdiv[2001][500];
int main()
{
	scanf("%d%d", &m, &n);
	for(int i = 1; i <= m; i++)
	{
		for(int j = 1; j <= m; j++)
		{
			if(i % j == 0)
				fdiv[i][flen[i]++] = j;
		}
	}
	dp[0][1] = 1;
	for(int i = 1; i <= n; i++)
	{
		for(int j = 1; j <= m; j++)
		{
			for(int k = 0; k < flen[j]; k++)
				dp[i][j] = (dp[i][j] + dp[i - 1][fdiv[j][k]]) % mod;
		}
	}
	for(int i = 1; i <= m; i++)
		ans = (ans + dp[n][i]) % mod;
	printf("%d", ans);
	getchar(); getchar();
	return 0;
}
