#include <iostream>
#include <vector>
#include <string>
#include <set>
#include <algorithm>


using namespace std;

int N, K, w[64], T;
double A, x, m[64][64][64], C[64][64];

int main()
{
	for (int n = 0; n < 64; n += 1)
	{
		C[n][0] = 1;
		C[n][n] = 1;

		for (int r = 1; r <= n/2; r += 1)
		{
			C[n][r] = C[n-1][r-1]+C[n-1][r];
			C[n][n-r] = C[n][r];
			//cout << n << ' ' << r << ':' << C[n][r] << '\n';
		}
	}
	cin >> N;

	for (int i = 1; i <= N; i += 1)
	{
		cin >> w[i];
		T += w[i];
	}


	cin >> K;

	if (T <= K)
	{
		A = N;
		goto last;
	}

	m[0][0][0] = 1;

	for (int i = 1; i <= N; i += 1)
	{
		for (int a = 0; a < N; a += 1)
		{
			x = 0;
			for (int r = 0; r <= K; r += 1)
			{
				for (int j = 1; j <= N; j += 1)
				{
					m[j][r][a] = m[j-1][r][a];
					if (i != j && r >= w[j]&&  a >= 1)
					{
						m[j][r][a] += m[j-1][r-w[j]][a-1];
					}
					//if(m[j][r][a])
						//cout << i << ' ' << j << ' ' << r << ' ' << a << ' ' << m[j][r][a] << '\n';
				}
				if ( r+w[i] > K)
				{
					x += m[N][r][a];
				}
			}
			A += (x*a/(N*1.0))/C[N-1][a];
		}

	}
	last:
	cout << A << '\n';

	return 0;
}
