#include <cstdio>
#include <algorithm>
#define REP(i,n) for (int i=1;i<=n;++i)
using namespace std;

const int MAXN=200020;
int n,ans;
int a[MAXN];

int main() {
	scanf("%d",&n);
	REP(i,n) scanf("%d",&a[i]);
	sort(a+1,a+n+1);
	REP(i,n-1) if (i==1 || a[i]!=a[i-1])
		for (int k=a[i]+a[i];k-a[i]<=a[n];k+=a[i]) {
			int tmp=i+1;
			for (int j=(1<<18);j;j>>=1)
				if (tmp+j<=n && a[tmp+j]<k) tmp+=j;
			ans=max(ans,a[tmp]%a[i]);
		}
	printf("%d\n",ans);
	return 0;
}
