#include <iostream>
using namespace std;
#include <algorithm>
typedef long long ll;
ll A[300000];
ll DP[5001][5001];
ll n, k;
int main()
{
    cin >> n >> k;
    for (int i=0; i<n; i++)
        cin >> A[i];
    sort(A, A+n);
    ll num=n/k;
    ll extra=n%k;
    for (ll i=1; i<=k; i++)
    {
        for (ll j=0; j<=min(i, extra); j++)
        {
            if (j==0) DP[i][j]=DP[i-1][j]+A[i*num-1]-A[(i-1)*num];
            else if (i==j)
                DP[i][j]=DP[i-1][j-1]+A[i*num-1+j]-A[(i-1)*num+j-1];
            else
                DP[i][j]=min(DP[i-1][j]+A[i*num-1+j]-A[(i-1)*num+j], DP[i-1][j-1]+A[i*num-1+j]-A[(i-1)*num+j-1]);
        }
    }
    cout << DP[k][extra] << endl;
    //cout << k << " " << num << " " << extra << " " << DP[k][extra] << endl;
    //cout << DP[1][0] << DP[1][1] << DP[2][0] << DP[2][1] << endl;
}
