#include<bits/stdc++.h>
using namespace std;
typedef long long ll;
inline int in(){
    int x;
    scanf("%d",&x);
    return x;
}
const int N=3005,mod=1e9+7;
inline int add(int a,int b){return a+b>=mod?a+b-mod:a+b;}
int n,a[N];
int pw[N];
int main(){
    n=in();
    pw[0]=1;
    for(int i=1;i<=n;i++)a[i]=in(),pw[i]=add(pw[i-1],pw[i-1]);
    int ans=0;
    for(int l=1;l<=n;l++){
        for(int r=l+1;r<=n;r++){
            int x=lower_bound(a+1,a+n+1,a[l]+a[l]-a[r])-a-1;
            int y=lower_bound(a+1,a+n+1,a[r]+a[r]-a[l])-a;
            ans=add(ans,pw[x+n-y+1]);
        }
    }
    cout<<ans<<endl;
    return 0;
}
