lng powmod(lng n, lng k, lng mod){
    if (k==0)return 1;
    if (k==1)return n%mod;
    lng t = powmod(n, k/2, mod);
    t*=t;
    t%=mod;
    if (k%2)t*=n;
    t%=mod;
    return t;
}

