#include<bits/stdc++.h>
#define pb push_back
#define eb emplace_back
#define SZ(x) ((int)(x).size())
#define each(x,v) for(auto&x:v)
#define rep(i,a,b) for(int i=(a);i<=(b);++i)
#define per(i,a,b) for(int i=(a);i>=(b);--i)
using namespace std;
const int N=1000005;
int n,a[N],b[N];
int main(){
	scanf("%d",&n);
	rep(i,1,n)scanf("%d",&a[i]),++b[a[i]];
	int ans=0;
	rep(i,1,1000000)if(!b[i]){
		int g=0;
		for(int j=i,k=1;j<=1000000;j+=i,++k){
			if(b[j])g=__gcd(g,k);
		}
		if(g==1){
			++ans;
		}
	}
	printf("%d\n",ans);
	return 0;
}
