#include<cstdio>
#include<cstdlib>
#include<cstring>
#include<algorithm>
#include<iostream>
using namespace std;

int a[10];
long long f[10][200];
long long C[300][300];
int main()
{
    long long mod=1000000007;
    for (int i=1;i<=200;i++)
    {
        C[i][i]=C[i][0]=1;
    }
    C[0][0]=1;
    for (int i=2;i<=200;i++)
        for (int j=1;j<i;j++)
        {
            C[i][j]=C[i-1][j-1]+C[i-1][j];
            C[i][j]%=mod;
        }
    int n;
    cin>>n;
    int tot=0;
    for (int i=0;i<10;i++)
    {
        cin>>a[i];
        tot+=a[i];
    }
    memset(f,0,sizeof(f));
    for(int i=a[9];i<=n;i++)
    {
        f[9][i]=1;
    }
    for (int i=8;i>0;i--)
    {
        if (a[i]==0) f[i][0]=f[i+1][0];
        for (int j=1;j<=n;j++)
            for (int k=a[i];k<=j;k++)
            {
                f[i][j]+=f[i+1][j-k]*C[j][k];
                f[i][j]%=mod;
            }
    }
    for (int j=2;j<=n;j++)
        for (int k=a[0];k<j;k++)
        {
            f[0][j]+=f[1][j-k]*C[j-1][k];
            f[0][j]%=mod;
        }
   // cout<<f[1][1]<<' '<<f[1][2]<<endl;
    long long ans=0;
    if (a[0]==0)
    {
        ans+=f[1][1];
    }
    for (int i=2;i<=n;i++)
    {
        ans+=f[0][i];
        ans%=mod;
    }
    cout<<ans%mod<<endl;
}
