#include<bits/stdc++.h>
#define N 100010
using namespace std;
int t,n,k,a[N];
bool ans;
int main(){
	cin>>t;
	while (t--){
		cin>>n>>k;ans=0;
		for (int i=1;i<=n;i++){
			cin>>a[i];
			if (a[i]==k)ans=1;
			if (a[i]>=k)a[i]=1;else a[i]=0;
		}
		if (!ans)puts("no");
		else{
			a[n+1]=a[n+2]=0;
			for (int i=1;i<=n;i++)if (a[i]&&(a[i+1]|a[i+2]))ans=0;
			if (n==1&&a[1])ans=0;
			if (ans)puts("no");else puts("yes");
		}
	}
	return 0;
}
