#include <iostream>
#include <cstdio>
#include <cstdlib>
#include <cstring>
#include <cmath>
#define maxn 100010

using namespace std;

int n;
int f[maxn];

int main() {
    //freopen("d.in","r",stdin);

    for (int i=1;i<=100000;++i) f[i]=-100000;
    scanf("%d",&n);
    for (int ii=1;ii<=n;++ii) {
        int x,y;
        scanf("%d%d",&x,&y);
        int cnt=0;
        for (int i=1;i<=(int)sqrt(x);++i)
            if (x % i==0) {
                int p=i,q=x/i;
                if (f[p]<ii-y) cnt++;
                f[p]=ii;
                if (q!=p) {
                    if (f[q]<ii-y) cnt++;
                    f[q]=ii;
                }
            }
        printf("%d\n",cnt);
    }

    return 0;
}
