}

void Mul (Long &a, int b)
{
    int cur = 0;

    for (int i = 0;i < a.size;i++)
    {
        a.x[i] = a.x[i] * b + cur;
        cur = a.x[i] / BASE;
        a.x[i] %= BASE;
    }
    if (cur > 0)
        a.x[a.size++] = cur;
}

void print (Long &a)
{
    printf ("%d", a.x[a.size-1]);

    for (int i = a.size - 2;i >= 0;i--)
        printf ("%04d", a.x[i]);
    printf ("\n");
}

