#include<bits/stdc++.h>
#define R register
#define N 100021
#define IT std::set<int>::iterator

std::set<int> st,ps;
int a[N],n,del[N];

inline int gcd(int a,int b){
	return !b?a:gcd(b,a%b);
}

inline void solve(){
	scanf("%d",&n);
	R int tp=0;
	ps.clear();
	st.clear();
	for(R int i=1;i<=n;++i) scanf("%d",&a[i]);
	for(R int i=1;i<=n;++i){
		ps.insert(i);
		R int xia=(i==n)?1:i+1;
		if(gcd(a[i],a[xia])==1) st.insert(i);
	}
	R int nw=1;
	while
	(1){
		if(!st.size()){
			printf("%d ",tp);
			for(R int _=1;_<=tp;++_) printf("%d ",del[_]);
			puts("");return;
		}
		if(ps.size()==1){
			if(a[nw]==1){
				ps.clear();
				st.clear();
				del[++tp]=nw;
			}
			else{
				;
			}
			continue;
		}
		IT qwq=st.lower_bound(nw); //找到往前第一对不合法的位置 pss
		if(qwq==st.end()) qwq=st.begin();
		R int pss=(*qwq);
//		printf("nw=%d, pss = %d\n",nw,pss);
		IT qaq=ps.lower_bound(pss); //下一个位置到底是什么
		++qaq;
		if(qaq==ps.end()) qaq=ps.begin();
		R int nxx=(*qaq); //找到了下一个位置 nxx
		++qaq;
		if(qaq==ps.end()) qaq=ps.begin();
		R int nnx=(*qaq); //下一个我们需要连上的位置
//		printf("nxx = %d, nnx = %d\n",nxx,nnx);
		//也就是删掉了 nxx
		del[++tp]=nxx;
		ps.erase(nxx);
		st.erase(pss); //删掉当前匹配
		if(gcd(a[nxx],a[nnx])==1) st.erase(nxx);
//		printf("sss %d\n",st.size());
		if(gcd(a[pss],a[nnx])==1) st.insert(pss); //新建立的联系。
		nw=nnx;
	} //每次都至少删一个数 
}

int main(){
	R int T;
	std::cin>>T;
	while(T--)
		solve();
}
