#include <bits/stdc++.h>
using namespace std;
vector<pair<int, int> > a[300020];
queue<int> q;
bool v[300020];
bool f[300020];
int n, k, d, x, y;
int main() {
	scanf("%d%d%d", &n, &k, &d);
	for (int i = 0; i < k; i++) {
		int x;
		scanf("%d", &x);
		q.push(x);
		v[x] = true;
	}
	for (int i = 1; i < n; i++) {
		scanf("%d%d", &x, &y);
		a[x].push_back(make_pair(y, i));
		a[y].push_back(make_pair(x, i));
	}
	while (q.size()) {
		int u = q.front();
		q.pop();
		for (int i = 0; i < a[u].size(); i++) {
			if (!v[a[u][i].first]) {
				f[a[u][i].second] = true;
				q.push(a[u][i].first);
				v[a[u][i].first] = true;
			}
		}
	}
	int nitamashabiba = 0;
	for (int i = 1; i < n; i++) {
		if (!f[i]) {
			nitamashabiba++;
		}
	}
	printf("%d\n", nitamashabiba);
	for (int i = 1; i < n; i++) {
		if (!f[i]) {
			printf("%d ", i);
		}
	}
	return 0;
}
