#include <bits/stdc++.h>
using namespace std;
template <typename T> void read(T &t) {
	t=0; char ch=getchar(); int f=1;
	while (ch<'0'||ch>'9') { if (ch=='-') f=-1; ch=getchar(); }
	do { (t*=10)+=ch-'0'; ch=getchar(); } while ('0'<=ch&&ch<='9'); t*=f;
}
typedef long long ll;
const int maxn=(1e5)+10;
const ll INF=(1e10);
int n,k,tot;
ll a[maxn],cnt[maxn],ans,b,d[maxn];
int main() {
	//freopen("1.txt","r",stdin);
	read(n); read(k);
	for (int i=1;i<=n;i++) read(a[i]),cnt[a[i]]++;
	for (int x=1;;x++) {
		ll tmp=1;
		for (int i=1;i<=k;i++) {
			tmp*=x;
			if (tmp>=maxn) break;
		}
		if (tmp>=maxn) break;
		tot=x; d[x]=tmp;
	}
	for (int i=1;i<=n;i++) {
		ll z=2,tmp=a[i],y=1;
		while (z*z<=tmp) {
			if (tmp%z==0) {
				b=0;
				while (tmp%z==0) tmp/=z,b++;
				b=(k-b%k)%k;
				while (b--) {
					y=y*z;
					if (y>=maxn) break;
				}
			}
			if (y>=maxn) break;
			if (z==2) z=3; else z+=2;
		}
		if (y>=maxn) continue;
		if (tmp>1) {
			b=k-1;
			while (b--) {
				y=y*tmp;
				if (y>=maxn) break;
			}
		}
		if (y>=maxn) continue;
		//printf("%lld %lld\n",a[i],y);
		for (int x=1;x<=tot;x++) {
			tmp=y*d[x];
			if (tmp>=maxn) break;
			if (tmp==a[i]) ans+=cnt[tmp]-1;
			else ans+=cnt[tmp];
		}
	}	
	printf("%lld\n",ans/2);
	return 0;
}
