#include<cstdio>
#include<cstdlib>
#include<cstring>
#include<algorithm>
#include<iostream>
#include<fstream>
#include<map>
#include<ctime>
#include<set>
#include<queue>
#include<cmath>
#include<vector>
#include<bitset>
#include<functional>
#define x first
#define y second
#define mp make_pair
#define pb push_back
#define REP(i,l,r) for((i)=(l);(i)<=(r);++(i))
#define REP2(i,l,r) for((i)=(l);(i)!=(r);++(i))
using namespace std;

typedef long long LL;
typedef double ld;

const int MAX=1000000+10;

int n;
int id[MAX],next[MAX];
int del[MAX];

int main()
{
#ifndef ONLINE_JUDGE
	freopen("input.txt","r",stdin);freopen("output.txt","w",stdout);
#endif
	int i;
	scanf("%d",&n);
	int step=0;
	vector< pair<int,int> > B;
	REP(i,1,n)
		scanf("%d",&id[i]);
	REP(i,1,n)
	{
		next[i]=i+1;
		if(next[i]!=n+1 && id[i]>id[next[i]])
			B.pb(mp(i,next[i]));
	}
	while(!B.empty())
	{
		step++;
		sort(B.begin(),B.end(),greater< pair<int,int> >());
		vector< pair<int,int> > C;
		int i;
		REP2(i,0,(int)B.size())
		{
			int x=B[i].x;
			int y=B[i].y;
			del[y]=1;
			while(del[next[x]])
				next[x]=next[next[x]];
			if(next[x]!=n+1 && id[x]>id[next[x]])
				C.pb(mp(x,next[x]));
		}
		B=C;
	}
	cout<<step<<endl;
	return 0;
}
