#include <bits/stdc++.h>
using namespace std;

int main() {
    cin.sync_with_stdio(0);
    string s; cin >> s;
    int N =s.length();

    vector<int> T(N+1,0);
    int g =(int)(s[N-1] == 'F'), lastM =N, ans =0;
    for(int i =N-2; i >= 0; i--) {
        if(s[i] == 'F') {g++; continue;}
        // collide directly
        if(s[i+1] == 'M') T[i] =T[i+1]+(int)(g > 0); 
        // dont collide
        else if(g > 0) {
            T[i] =g;
            if(lastM < N && T[i] <= T[lastM]) T[i] =T[lastM]+1;}
        ans =T[i];
        lastM =i;}
    cout << ans << "\n";
    return 0;}
