using namespace std;
typedef long long LL;
typedef pair<int, int> PII;
#define MP make_pair
#define FOR(v,p,k) for(int v=p;v<=k;++v)
#define FORD(v,p,k) for(int v=p;v>=k;--v)
#define REP(i,n) for(int i=0;i<(n);++i)
#define VAR(v,i) __typeof(i) v=(i)
#define FOREACH(i,c) for(VAR(i,(c).begin());i!=(c).end();++i)
#define PB push_back
#define ST first
#define ND second
#define SIZE(x) (int)x.size()
#define ALL(c) c.begin(),c.end()
#define int long long

#undef int 
int main() {
#define int long long
	ios_base::sync_with_stdio(0);
	int n, k;
	cin >> n >> k;
	set<int> toCheck;
	REP(i, sqrt(k)) {
		int l = i + 2;
		int co = 1;
		while (k % l == 0) {
			k /= l;
			co *= l;
		}
		if(co != 1)
			toCheck.insert(co);
	}
	if (k != 1) {
		toCheck.insert(k);
	}
	vector<int> inp(n);
	REP(i, n) {
		cin >> inp[i];
		vector<int> toErase;
		for (int num : toCheck) {
			if (inp[i] % num == 0) {
				toErase.push_back(num);
			}
		}
		for (int el : toErase) {
			toCheck.erase(el);
		}
	}
	if (toCheck.empty()) {
		cout << "Yes";
	} else {
		cout << "No";
	}
	return 0;
}
