#include <cstdio>
#include <cstring>
#include <algorithm>
#include <cstdlib>
#define eps 1e-8

using namespace std;

bool ask(int x) {
  printf("%d\n",x);
  fflush(stdout);
  int t;
  scanf("%d",&t);
  if (!t) exit(0);
  return (t<0)?0:1;
}

bool ok[35];

int main() {
  int m,n;
  scanf("%d%d",&m,&n);
  for(int i=1;i<=n;i++) ok[i]=ask(1);
  int l=1,r=m,cnt=0;
  while (l<r) {
  	int mid=((l+r)>>1);
  	cnt++;
  	if (cnt>n) cnt=1;
  	if (ask(mid)^ok[cnt]) r=mid; else l=mid+1;
  }
  ask(l);
  return 0;
}
