#include<bits/stdc++.h>
using namespace std;
typedef long long LL;
typedef pair<int,int> PII;
#define MP make_pair
#define PB push_back
#define AA first
#define BB second
#define OP begin()
#define ED end()
#define SZ size()
#define cmin(x,y) x=min(x,y)
#define cmax(x,y) x=max(x,y)
#define NAME ""
#define UsingFile 0
const LL MOD = 1000000007;
LL n,A,cf,cm,m;
pair<LL,int> a[100005];
int b[100005];
LL S[100005];
void build(LL e,int id){
	int i;
	for(i=id;i<n;i++)b[a[i].BB]=A;
	for(i=0;i<id;i++){
		if(a[i].AA<e)b[a[i].BB]=e;
		else b[a[i].BB]=a[i].AA;
	}
}
int main(){
    if(UsingFile)freopen(NAME".in","r",stdin);
    if(UsingFile)freopen(NAME".out","w",stdout);
    int i,j,k,_T;
    while(cin>>n>>A>>cf>>cm>>m){
    	for(i=0;i<n;i++)
    		cin>>a[i].AA,a[i].BB=i;
    	sort(a,a+n);
    	S[0]=a[0].AA;
    	for(i=1;i<n;i++)
    		S[i]=S[i-1]+a[i].AA;
    	LL rem=m;
    	LL ans=-1,_g=0,_i=n;
    	for(i=n;i>=0;i--){
    		if(i<n)rem-=A-a[i].AA;
    		if(rem<0)break;
    		LL le=0,re=i-1,be=-1;
    		while(re>=le){
    			if(re-le<=1){
    				if(a[re].AA*(re+1)-S[re]<=rem)be=re;
    				else be=le;
    				break;
    			}
    			LL me=(le+re)>>1;
    			if(a[me].AA*(me+1)-S[me]<=rem)le=me;
    			else re=me;
    		}
    		LL ge;
    		if(~be)ge=a[be].AA+(rem-(a[be].AA*(be+1)-S[be]))/(be+1);
    		else ge=A;
    		cmin(ge,A);
    		LL force=cm*ge+cf*(n-i);
    		if(force>ans)ans=force,_g=ge,_i=i;
    	}
    	build(_g,_i);
    	cout<<ans<<"\n";
    	for(i=0;i<n;i++)cout<<b[i]<<" ";cout<<"\n";
    }
    return 0;
}
