//author: wuge with noi2022 cu
//https://www.luogu.com.cn/team/48234
#include<bits/stdc++.h>
using namespace std;
#define M998 const int mod=998244353
#define M197 const int mod=1e9+7
#define int long long
#define genshin getchar
#define impact isdigit
inline int read()
{
     int shaya=0,wugecu=1;//5 spaces
  char cirno_9=genshin();//2 spaces
        while(!impact(cirno_9))//8 spaces
  {if(cirno_9=='-')wugecu=-1;cirno_9=genshin();}//2 spaces
  while(impact(cirno_9)) shaya=shaya*10+cirno_9-'0',cirno_9=genshin();//2 spaces
 return shaya*wugecu;//1 space
}
int n,k;
int a[1000003];
bool chk()
{
	vector<pair<int,int>> b;
#define fi first
#define se second
	int val=a[k];
	for(int i=k+1; i<=n;)
	{
		int sum=0,mn=0;
		while(i<=n&&sum<=0)
		{
			sum+=a[i++];
			mn=min(sum,mn);
		}
		if(sum>0)
		{
			b.push_back(make_pair(sum,mn));
		}
	}
	auto it=b.begin();
	for(int i=k-1; i>=1; --i)
	{
		while(it!=b.end()&&val+(it->se)>=0)
		{
			val+=it->fi,++it;
		}
		if(val+a[i]<0) return 0;
		val+=a[i];
	}
	return 1;
}
void Meyi()
{
	n=read(),k=read();
	for(int i=1; i<=n; ++i) a[i]=read();
	//to left
	if(chk()) 
	{
		puts("YES");
		return ;
	}
	reverse(a+1,a+n+1);k=n+1-k;
	if(chk()) 
	{
		puts("YES");
		return ;
	}
	puts("NO");
	return ;	
}
signed main()
{
	int miHoYo=read();
	while(miHoYo--) Meyi();
	return 0;
}
