#include <bits/stdc++.h>

#define ll long long
using namespace std;

vector<int> t;
int n;

void init (int nn)
{
    n = nn;
    t.assign (n, 0);
}

int sum (int r)
{
    int result = 0;
    for (; r >= 0; r = (r & (r+1)) - 1)
        result += t[r];
    return result;
}

void inc (int i, int delta)
{
    for (; i < n; i = (i | (i+1)))
        t[i] += delta;
}

int sum (int l, int r)
{
    return sum (r) - sum (l-1);
}

void init (vector<int> a)
{
    init ((int) a.size());
    for (unsigned i = 0; i < a.size(); i++)
        inc (i, a[i]);
}

int main() {
#ifdef __APPLE__
    freopen("input.txt", "r", stdin);
    freopen("output.txt", "w", stdout);
#endif

    int n;
    scanf("%d", &n);
    vector<int> a(n);
    for (int i = 0; i < n; ++i)
        scanf("%d", &a[i]);
    init(n);
    printf("1 ");
    for (int i = 0; i < n; ++i) {
        inc(a[i] - 1, 1);
        int tl = 0, tr = n;
        bool ok = false;
        if (sum(n - 1, n - 1)) {
            ok = true;
            while (tr - tl > 1) {
                int tm = (tl + tr) / 2;
                if (sum(n - 1 - tm, n - 1) == tm + 1)
                    tl = tm;
                else
                    tr = tm;
            }
        }
        printf("%d ", i + 2 - (tl + (int)ok));
    }

    return 0;
}
