#include <stdio.h>
#include <stdlib.h>

int n, i, Now, Tag, a[100005];
char Ans[100005];

int main()
{
	scanf("%d", &n);
	for (i = 1; i <= n; i++)
		scanf("%d", &a[i]);
	if (n == 1)
	{
		printf("+\n");
		return 0;
	}

	Now = a[n];
	Ans[n] = '+';
	for (i = n - 1; i >= 1; i--)
	{
		if (Now > a[i])
			Ans[i] = '-', Now -= a[i];
		else
			Ans[i] = '+', Now = a[i] - Now;
	}

	Tag = 0;
	for (i = 1; i <= n; i++)
	{
		if (Tag == 0)
			printf("%c", Ans[i]);
		else
			printf("%c", '+' + '-' - Ans[i]);
		if (Ans[i] == '+')
			Tag = 1 - Tag;
	}

	printf("\n");
	return 0;
}
