#include <bits/stdc++.h>
#define sz(x) ((int)x.size())
#define all(x) (x).begin(), (x).end()
using namespace std;
typedef long long ll;
typedef long double ld;
int n;
int main() {
	scanf("%d", &n);
	if(n / 2 % 2) {
		printf("! -1");
		fflush(stdout);
		return 0;
	}
	int v1, v2;
	printf("? 1\n"); fflush(stdout);
	scanf("%d", &v1);
	printf("? %d\n", n/2+1); fflush(stdout);
	scanf("%d", &v2);
	if(v1 == v2) {
		printf("! 1"); fflush(stdout);
		return 0;
	}
	int r1 = v1, r2 = v2;
	int lo = 1, hi = n / 2;
	while(lo < hi) {
		int mid = (lo + hi) / 2;
		printf("? %d\n", mid); fflush(stdout);
		scanf("%d", &v1);
		printf("? %d\n", (mid+n/2-1)%n+1); fflush(stdout);
		scanf("%d", &v2);
		if(v1 == v2) {
			printf("! %d\n", mid); fflush(stdout);
			return 0;
		}
		if(r1 - r2 < 0) {
			if(v1 - v2 < 0) lo = mid + 1;
			else hi = mid - 1;
		} else {
			if(v1 - v2 > 0) lo = mid + 1;
			else hi = mid - 1;
		}
	}
	printf("! %d\n", lo); fflush(stdout);
	return 0;
}

