#include <bits/stdc++.h>
#define int long long
using namespace std;
inline int cal(int x)
{
	int a=x/3,b=x/3,c=x/3;
	if(a+b+c<x) ++a;
	if(a+b+c<x) ++b;
	if(a+b+c<x) ++c;
	return (a*b+b*c+c*a)*2;
}
signed main()
{
	ios::sync_with_stdio(false);
	cin.tie(0);
	int T;
	cin >> T;
	while(T--)
	{
		int n;
		cin >> n;
		int x=0,y=0,z=0,flag=0;
		int l=0,r=1e5,ans=0;
		while(l<=r)
		{
			int mid=(l+r)/2;
			if(cal(mid)>=n) ans=mid,r=mid-1;
			else l=mid+1;
		}
		cout << ans << "\n";
	}
	return 0;
}
