#include <bits/stdc++.h>
using namespace std;

const int mxN=2e3;
int n, k, b[mxN+1][mxN+1];
string s[mxN];

int main() {
	ios::sync_with_stdio(0);
	cin.tie(0);

	cin >> n >> k;
	for(int i=0; i<n; ++i)
		cin >> s[i];
	for(int i=0; i<n; ++i) {
		int l=0;
		while(l<n&&s[i][l]=='W')
			++l;
		if(l>=n) {
			++b[0][0];
			continue;
		}
		int r=n-1;
		while(~r&&s[i][r]=='W')
			--r;
		if(r-l+1>k)
			continue;
		//i-k+1, i
		//r-k+1, l
		int l1=max(i-k+1, 0), r1=i+1;
		int l2=max(r-k+1, 0), r2=l+1;
		++b[l1][l2];
		--b[l1][r2];
		--b[r1][l2];
		++b[r1][r2];
	}
	for(int j=0; j<n; ++j) {
		int l=0;
		while(l<n&&s[l][j]=='W')
			++l;
		if(l>=n) {
			++b[0][0];
			continue;
		}
		int r=n-1;
		while(~r&&s[r][j]=='W')
			--r;
		if(r-l+1>k)
			continue;
		//r-k+1, l
		//j-k+1, j
		int l1=max(r-k+1, 0), r1=l+1;
		int l2=max(j-k+1, 0), r2=j+1;
		++b[l1][l2];
		--b[l1][r2];
		--b[r1][l2];
		++b[r1][r2];
	}
	for(int i=0; i<n; ++i)
		for(int j=0; j<n; ++j)
			b[i][j+1]+=b[i][j];
	for(int i=0; i<n; ++i)
		for(int j=0; j<n; ++j)
			b[i+1][j]+=b[i][j];
	int ans=0;
	for(int i=0; i<n; ++i)
		for(int j=0; j<n; ++j)
			ans=max(b[i][j], ans);
	cout << ans;
}
