#include<cstdio>
#include<cstring>
#include<algorithm>
#include<iostream>
#define rep(i,a,b) for(int i=(a);i<=(b);i++)
#define per(i,a,b) for(int i=(a);i>=(b);i--)
#define ff first
#define ss second
using namespace std;
const int N=200005;
const int inf=1e9+7;
typedef long long ll;
typedef long double lb;
inline void read(int &x){x=0; char ch=getchar();while(ch<'0') ch=getchar();while(ch>='0'){x=x*10+ch-48; ch=getchar();}}
//------------------------------------------head---------------------------------------------------//
int ti[N],n,ans;
struct node
{
	int x,y;
	int dis;
}q[N];
inline bool cmp(const node &x,const node &y)
{
	return x.dis<y.dis;
}
int main()
{
	int k; read(n); read(k); int cnt=0; int la=0;
	rep(i,1,n)
	{
		scanf("%d",&ti[i]);
		if(ti[i]<0)
		{
			if(cnt)
			{
				q[cnt].x=la; q[cnt].y=i;
				q[cnt].dis=i-la-1;
			}
			cnt++;
			la=i;
		}
	}
	if(!la) return puts("0"),0;
	if(cnt>k) return puts("-1"),0;
	ans=cnt*2; int res=cnt*2;
	sort(q+1,q+cnt,cmp); int tt=k-cnt; //printf("%d\n",tt);
	rep(i,1,cnt-1)
	{
	//	printf("Case 0:%d\n",q[i].dis);
		if(n-la<=tt) ans=min(ans,res-1);
		else ans=min(ans,res);
		if(q[i].dis<=tt)
		{
			tt-=q[i].dis;
			res-=2;
		}
	}if(n-la<=tt) ans=min(ans,res-1);
	else ans=min(ans,res);
	printf("%d\n",ans);
	return 0;
}
